import React, { useState, useEffect } from "react";
import { ethers } from "ethers"; // Updated import to ethers directly
import "./App.css";

const App = () => {
  const [contract, setContract] = useState(null);
  const [userId, setUserId] = useState(""); // New state for userId
  const [userType, setUserType] = useState(""); // New state for userType

  const [productId, setProductId] = useState("");
  const [productDetails, setProductDetails] = useState(null);

  const [productName, setProductName] = useState("");
  const [supplierName, setSupplierName] = useState("");
  const [certificationDetails, setCertificationDetails] = useState("");

  const [location, setLocation] = useState("");
  const [checkpoint, setCheckpoint] = useState("");

  const [certifyingAuthority, setCertifyingAuthority] = useState("");
  const [certificationType, setCertificationType] = useState("");

  // Define contract address here
  const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; // Example address

  // ABI (replace this with your actual ABI)
  const contractABI = [
    {
      "inputs": [],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        }
      ],
      "name": "registerProduct",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "location",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "checkpoint",
          "type": "string"
        }
      ],
      "name": "updateLocation",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certifyingAuthority",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationType",
          "type": "string"
        }
      ],
      "name": "addCertification",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        }
      ],
      "name": "getProduct",
      "outputs": [
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "currentCheckpoint",
          "type": "string"
        },
        {
          "internalType": "bool",
          "name": "delivered",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "userId",
          "type": "string"
        },
        {
          "internalType": "uint8",
          "name": "userType",
          "type": "uint8"
        }
      ],
      "name": "registerUser",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    }
  ];

  // Connect to the contract
  const connectContract = async () => {
    if (!window.ethereum) {
      console.error("MetaMask is not installed or detected.");
      alert("MetaMask is not installed or detected.");
      return;
    }

    try {
      // Request account access if necessary
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      if (accounts.length === 0) {
        throw new Error("No account found in MetaMask.");
      }

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();

      const contractInstance = new ethers.Contract(
        contractAddress,
        contractABI,
        signer
      );

      setContract(contractInstance);
      console.log("Contract connected successfully.");
    } catch (error) {
      console.error("Error connecting to MetaMask or the contract:", error);
      alert(`Failed to connect to MetaMask or the contract: ${error.message}`);
    }
  };

  // Register a user (Supplier, Manufacturer, etc.)
  const registerUser = async () => {
    if (!contract) {
      console.error("Contract is not initialized.");
      return;
    }

    try {
      // Convert userType to an integer (0: Supplier, 1: Manufacturer, 2: Regulator, etc.)
      const userTypeEnum = {
        Supplier: 0,
        Manufacturer: 1,
        Regulator: 2,
        Logistics: 3,
        Retailer: 4
      };

      const tx = await contract.registerUser(userId, userTypeEnum[userType]);
      await tx.wait();
      alert("User registered successfully!");
    } catch (error) {
      console.error("Error registering user:", error);
    }
  };

  // Register a product
  const registerProduct = async () => {
    if (!contract) {
      console.error("Contract is not initialized.");
      return;
    }

    try {
      const tx = await contract.registerProduct(
        productId,
        productName,
        supplierName,
        certificationDetails
      );
      await tx.wait();
      alert("Product registered successfully!");
    } catch (error) {
      console.error("Error registering product:", error);
    }
  };

  // Fetch product details
  const getProductDetails = async () => {
    try {
      if (!productId) {
        alert("Please enter a valid product ID.");
        return;
      }

      // Fetch product details from the contract
      const product = await contract.getProduct(productId);

      // Log the product response to check its structure
      console.log("Product details:", product);

      // Check if the product exists and has valid data
      if (product && product[0] && product[0] !== "0x" && product[0] !== "") {
        setProductDetails({
          productName: product[0],
          supplierName: product[1],
          certificationDetails: product[2],
          currentCheckpoint: product[3],
          isDelivered: product[4] ? "Delivered" : "Not Delivered",
        });
      } else {
        alert("No product found with the provided ID.");
      }
    } catch (error) {
      console.error("Error fetching product details:", error);
      alert(`Error fetching product details: ${error.message}`);
    }
  };

  useEffect(() => {
    connectContract();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Supply Chain Management</h1>

      {/* Register User */}
      <div className="mt-4">
        <h3>Register User</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="User ID"
            className="form-control my-2"
            onChange={(e) => setUserId(e.target.value)}
          />
          <select
            className="form-control my-2"
            onChange={(e) => setUserType(e.target.value)}
          >
            <option value="">Select User Type</option>
            <option value="Supplier">Supplier</option>
            <option value="Manufacturer">Manufacturer</option>
            <option value="Regulator">Regulator</option>
            <option value="Logistics">Logistics</option>
            <option value="Retailer">Retailer</option>
          </select>
          <button className="btn btn-primary" onClick={registerUser}>
            Register User
          </button>
        </div>
      </div>

      {/* Register Product */}
      <div className="mt-4">
        <h3>Register Product</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Product ID"
            className="form-control my-2"
            onChange={(e) => setProductId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Product Name"
            className="form-control my-2"
            onChange={(e) => setProductName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Supplier Name"
            className="form-control my-2"
            onChange={(e) => setSupplierName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Certification Details"
            className="form-control my-2"
            onChange={(e) => setCertificationDetails(e.target.value)}
          />
          <button className="btn btn-primary" onClick={registerProduct}>
            Register Product
          </button>
        </div>
      </div>

      {/* Fetch Product Details */}
      <div className="mt-4">
        <h3>Get Product Details</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Enter Product ID"
            className="form-control my-2"
            onChange={(e) => setProductId(e.target.value)}
          />
          <button className="btn btn-primary" onClick={getProductDetails}>
            Fetch Details
          </button>
          {productDetails && (
            <div className="mt-3">
              <h5>Product Name: {productDetails.productName}</h5>
              <h5>Supplier Name: {productDetails.supplierName}</h5>
              <h5>Certification Details: {productDetails.certificationDetails}</h5>
              <h5>Current Checkpoint: {productDetails.currentCheckpoint}</h5>
              <h5>Delivered: {productDetails.isDelivered ? "Yes" : "No"}</h5>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
