import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import RegisterUser from "./component/RegisterUser";
import RegisterProduct from "./component/RegisterProduct";
import UpdateLocation from "./component/UpdateLocation";
import AddCertification from "./component/AddCertification";
import ProductDetails from "./component/ProductDetails";

const App = () => {
  // State variables remain the same
  const [contract, setContract] = useState(null);
  const [productId, setProductId] = useState("");
  const [productDetails, setProductDetails] = useState(null);

  const [productName, setProductName] = useState("");
  const [supplierName, setSupplierName] = useState("");
  const [certificationDetails, setCertificationDetails] = useState("");

  const [location, setLocation] = useState("");
  const [checkpoint, setCheckpoint] = useState("");

  const [certifyingAuthority, setCertifyingAuthority] = useState("");
  const [certificationType, setCertificationType] = useState("");

  const [userType, setUserType] = useState("");
  const [userName, setUserName] = useState("");

  const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3";

  const contractABI = [
    {
      "inputs": [],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "certifyingAuthority",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "certificationType",
          "type": "string"
        }
      ],
      "name": "CertificationAdded",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "location",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "checkpoint",
          "type": "string"
        }
      ],
      "name": "LocationUpdated",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "location",
          "type": "string"
        }
      ],
      "name": "ProductDelivered",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": false,
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "indexed": false,
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "indexed": true,
          "internalType": "address",
          "name": "owner",
          "type": "address"
        }
      ],
      "name": "ProductRegistered",
      "type": "event"
    },
    {
      "anonymous": false,
      "inputs": [
        {
          "indexed": true,
          "internalType": "address",
          "name": "user",
          "type": "address"
        },
        {
          "indexed": false,
          "internalType": "enum SupplyChain.UserType",
          "name": "userType",
          "type": "uint8"
        }
      ],
      "name": "UserRegistered",
      "type": "event"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certifyingAuthority",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationType",
          "type": "string"
        }
      ],
      "name": "addCertification",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        }
      ],
      "name": "getProduct",
      "outputs": [
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "currentCheckpoint",
          "type": "string"
        },
        {
          "internalType": "bool",
          "name": "isDelivered",
          "type": "bool"
        },
        {
          "components": [
            {
              "internalType": "string",
              "name": "certifyingAuthority",
              "type": "string"
            },
            {
              "internalType": "string",
              "name": "certificationType",
              "type": "string"
            },
            {
              "internalType": "uint256",
              "name": "timestamp",
              "type": "uint256"
            }
          ],
          "internalType": "struct SupplyChain.Certification[]",
          "name": "certifications",
          "type": "tuple[]"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "address",
          "name": "user",
          "type": "address"
        }
      ],
      "name": "getUserType",
      "outputs": [
        {
          "internalType": "enum SupplyChain.UserType",
          "name": "",
          "type": "uint8"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "location",
          "type": "string"
        }
      ],
      "name": "markAsDelivered",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [],
      "name": "owner",
      "outputs": [
        {
          "internalType": "address",
          "name": "",
          "type": "address"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        }
      ],
      "name": "registerProduct",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "enum SupplyChain.UserType",
          "name": "userType",
          "type": "uint8"
        }
      ],
      "name": "registerUser",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "location",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "checkpoint",
          "type": "string"
        }
      ],
      "name": "updateLocation",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    }
  ];

  const connectContract = async () => {
    if (!window.ethereum) {
      alert("MetaMask not installed.");
      return;
    }

    try {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = provider.getSigner();
      const contractInstance = new ethers.Contract(contractAddress, contractABI, signer);
      setContract(contractInstance);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  useEffect(() => {
    connectContract();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Supply Chain Management</h1>
      <RegisterUser
        setUserName={setUserName}
        setUserType={setUserType}
        registerUser={() => {
          if (contract) contract.registerUser(userType, userName);
        }}
        userType={userType}
      />
      <RegisterProduct
        setProductId={setProductId}
        setProductName={setProductName}
        setSupplierName={setSupplierName}
        setCertificationDetails={setCertificationDetails}
        registerProduct={() => {
          if (contract) contract.registerProduct(productId, productName, supplierName, certificationDetails);
        }}
      />
      <UpdateLocation
        setLocation={setLocation}
        setCheckpoint={setCheckpoint}
        updateLocation={() => {
          if (contract) contract.updateLocation(productId, location, checkpoint);
        }}
      />
      <AddCertification
        setCertifyingAuthority={setCertifyingAuthority}
        setCertificationType={setCertificationType}
        addCertification={() => {
          if (contract) contract.addCertification(productId, certifyingAuthority, certificationType);
        }}
      />
      <div className="mt-4">
        <h3>Get Product Details</h3>
        <input
          type="text"
          placeholder="Enter Product ID"
          className="form-control my-2"
          onChange={(e) => setProductId(e.target.value)}
        />
        <button
          className="btn btn-primary"
          onClick={() => {
            if (contract) contract.getProduct(productId).then(setProductDetails);
          }}
        >
          Get Product Details
        </button>
        <ProductDetails productDetails={productDetails} />
      </div>
    </div>
  );
};

export default App;
