import React, { useState, useEffect } from "react";
import { ethers, BrowserProvider } from "ethers"; // Ensure correct import of BrowserProvider

import "./App.css";

const App = () => {
  const [contract, setContract] = useState(null);
  const [productId, setProductId] = useState("");
  const [productDetails, setProductDetails] = useState(null);

  const [productName, setProductName] = useState("");
  const [supplierName, setSupplierName] = useState("");
  const [certificationDetails, setCertificationDetails] = useState("");

  const [location, setLocation] = useState("");
  const [checkpoint, setCheckpoint] = useState("");

  const [certifyingAuthority, setCertifyingAuthority] = useState("");
  const [certificationType, setCertificationType] = useState("");

  // Define contract address here
  const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; // Example address

  // ABI (replace this with your actual ABI)
  const contractABI = [
    {
      "inputs": [],
      "stateMutability": "nonpayable",
      "type": "constructor"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        }
      ],
      "name": "registerProduct",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "location",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "checkpoint",
          "type": "string"
        }
      ],
      "name": "updateLocation",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certifyingAuthority",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationType",
          "type": "string"
        }
      ],
      "name": "addCertification",
      "outputs": [],
      "stateMutability": "nonpayable",
      "type": "function"
    },
    {
      "inputs": [
        {
          "internalType": "string",
          "name": "productId",
          "type": "string"
        }
      ],
      "name": "getProduct",
      "outputs": [
        {
          "internalType": "string",
          "name": "productName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "supplierName",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "certificationDetails",
          "type": "string"
        },
        {
          "internalType": "string",
          "name": "currentCheckpoint",
          "type": "string"
        },
        {
          "internalType": "bool",
          "name": "delivered",
          "type": "bool"
        }
      ],
      "stateMutability": "view",
      "type": "function"
    }
  ];

  // Connect to the contract
  const connectContract = async () => {
    if (!window.ethereum) {
      console.error("MetaMask is not installed or detected.");
      alert("MetaMask is not installed or detected.");
      return;
    }

    try {
      // Request account access if necessary
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      if (accounts.length === 0) {
        throw new Error("No account found in MetaMask.");
      }

      console.log("MetaMask accounts:", accounts);

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();

      const contractInstance = new ethers.Contract(
        contractAddress,
        contractABI,
        signer
      );

      setContract(contractInstance);
      console.log("Contract connected successfully.");
    } catch (error) {
      console.error("Error connecting to MetaMask or the contract:", error);
      alert(`Failed to connect to MetaMask or the contract: ${error.message}`);
    }
  };

  // Register a product
  const registerProduct = async () => {
    if (!contract) {
      console.error("Contract is not initialized.");
      return;
    }

    try {
      const tx = await contract.registerProduct(
        productId,
        productName,
        supplierName,
        certificationDetails
      );
      await tx.wait();
      alert("Product registered successfully!");
    } catch (error) {
      console.error("Error registering product:", error);
    }
  };

  // Update product location
  const updateLocation = async () => {
    try {
      const tx = await contract.updateLocation(productId, location, checkpoint);
      await tx.wait();
      alert("Location updated successfully!");
    } catch (error) {
      console.error("Error updating location:", error);
    }
  };

  // Add certification
  const addCertification = async () => {
    try {
      const tx = await contract.addCertification(
        productId,
        certifyingAuthority,
        certificationType
      );
      await tx.wait();
      alert("Certification added successfully!");
    } catch (error) {
      console.error("Error adding certification:", error);
    }
  };

  // Fetch product details
  const getProductDetails = async () => {
    try {
      const product = await contract.getProduct(productId);
      setProductDetails(product);
    } catch (error) {
      console.error("Error fetching product details:", error);
    }
  };

  useEffect(() => {
    connectContract();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Supply Chain Management</h1>

      <div className="mt-4">
        <h3>Register Product</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Product ID"
            className="form-control my-2"
            onChange={(e) => setProductId(e.target.value)}
          />
          <input
            type="text"
            placeholder="Product Name"
            className="form-control my-2"
            onChange={(e) => setProductName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Supplier Name"
            className="form-control my-2"
            onChange={(e) => setSupplierName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Certification Details"
            className="form-control my-2"
            onChange={(e) => setCertificationDetails(e.target.value)}
          />
          <button className="btn btn-primary" onClick={registerProduct}>
            Register Product
          </button>
        </div>
      </div>

      <div className="mt-4">
        <h3>Update Location</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Location"
            className="form-control my-2"
            onChange={(e) => setLocation(e.target.value)}
          />
          <input
            type="text"
            placeholder="Checkpoint"
            className="form-control my-2"
            onChange={(e) => setCheckpoint(e.target.value)}
          />
          <button className="btn btn-primary" onClick={updateLocation}>
            Update Location
          </button>
        </div>
      </div>

      <div className="mt-4">
        <h3>Add Certification</h3>
        <div className="form-group">
          <input
            type="text"
            placeholder="Certifying Authority"
            className="form-control my-2"
            onChange={(e) => setCertifyingAuthority(e.target.value)}
          />
          <input
            type="text"
            placeholder="Certification Type"
            className="form-control my-2"
            onChange={(e) => setCertificationType(e.target.value)}
          />
          <button className="btn btn-primary" onClick={addCertification}>
            Add Certification
          </button>
        </div>
      </div>

      <div className="mt-4">
        <h3>Get Product Details</h3>
        <div className="form-group">
          <button className="btn btn-primary" onClick={getProductDetails}>
            Fetch Details
          </button>
          {productDetails && (
            <div className="mt-3">
              <h5>Product Name: {productDetails[0]}</h5>
              <h5>Supplier Name: {productDetails[1]}</h5>
              <h5>Certification Details: {productDetails[2]}</h5>
              <h5>Current Checkpoint: {productDetails[3]}</h5>
              <h5>Delivered: {productDetails[4] ? "Yes" : "No"}</h5>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
